import { useEffect } from "react";
import Button from "./Button.js";

export default function ProductDetailInfo({ product, onProductAdd }) {
  const priced = product.price

  useEffect(() => {
    console.log(product)
  },[])

  return (
    <>
      <p>
        Sold at <strong>${priced}</strong> per
        piece.
      </p>
      <Button onClick={() => onProductAdd(product)}>${priced}</Button>
    </>
  );
}
